import React, { useEffect, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import api from "../Backendroutes";

function Profile() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    fetchProfile();
  }, []);

  const fetchProfile = async () => {
    try {
      console.log(sessionStorage.getItem)
      const res = await axios({
        method: api.getprofile.method,
        url: api.getprofile.url,

        headers: {
          Authorization: `Bearer ${sessionStorage.getItem("userinfo")}`,
        },
      });

      if (res.data.success) {
        setUser(res.data.body);
      } else {
        toast.error("Failed to load profile");
      }
    } catch (error) {
      console.log(error);
      toast.error("Error fetching profile");
    }
  };

  if (!user) {
    return <div className="text-center mt-5">Loading...</div>;
  }

  return (
    <div className="container mt-4">
      <div className="card shadow p-4">
        <div className="text-center">
          <img
            src={user.prevImg || "/default-avatar.png"}
            alt="Profile"
            className="rounded-circle mb-3"
            width="120"
            height="120"
          />
          <h3>{user.name}</h3>
          <p className="text-muted">{user.role.toUpperCase()}</p>
        </div>

        <hr />

        <div className="row">
          <div className="col-md-6 mb-3">
            <strong>UID:</strong>
            <p>{user.uid}</p>
          </div>

          <div className="col-md-6 mb-3">
            <strong>Email:</strong>
            <p>{user.email}</p>
          </div>

          <div className="col-md-6 mb-3">
            <strong>Mobile:</strong>
            <p>{user.mobile}</p>
          </div>

          {user.role === "doctor" && (
            <>
              <div className="col-md-6 mb-3">
                <strong>Specialization:</strong>
                <p>{user.specialization}</p>
              </div>

              <div className="col-md-6 mb-3">
                <strong>Experience:</strong>
                <p>{user.experience}</p>
              </div>

              <div className="col-md-6 mb-3">
                <strong>Qualification:</strong>
                <p>{user.qualification}</p>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default Profile;
