import React, { useState } from "react";
import axios from "axios";
import api from "../Backendroutes";
import { toast } from "react-toastify";

function AddPatient() {
  const [form, setForm] = useState({
    isOldPatient: false,
    docType: "OPD Registration",
    opdNo: "",
    date: "",
    time: "",
    casualty: "No",

    patientName: "",
    guardianName: "",

    houseNo: "",
    street: "",
    village: "",
    district: "",

    gender: "",
    maritalStatus: "",

    aadharNo: "",
    bloodGroup: "",

    mobile: "",
    email: "",

    dob: "",
    age: "",
  });

  const [generatedUhid, setGeneratedUhid] = useState("");
const [suggestions, setSuggestions] = useState([]);

const handleChange = async (e) => {
  const { name, value } = e.target;

  setForm({ ...form, [name]: value });

  // Only search if Old Patient = Yes and typing name
  if (form.isOldPatient && name === "patientName" && value.length > 1) {
    try {
      const res = await axios.get(
        `${api.baseUrl}/search-patient?name=${value}`,
      );

      if (res.data.success) {
        setSuggestions(res.data.body);
      }
    } catch (error) {
      console.log(error);
    }
  } else {
    setSuggestions([]);
  }
};
const selectPatient = (patient) => {
  setForm({
    ...patient,
  });

  setSuggestions([]);
};


  // Auto calculate age
  const handleDobChange = (e) => {
    const dob = e.target.value;
    const birthDate = new Date(dob);
    const today = new Date();
console.log(today,"today")
console.log(today.getFullYear(),"today getfull year")
console.log(birthDate,"birthda")
console.log(birthDate.getMonth(),"birthdate month")
    let age = today.getFullYear() - birthDate.getFullYear();
    const m = today.getMonth() - birthDate.getMonth();

    if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) {
      age--;
    }

    setForm({ ...form, dob, age });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post(api.addpatient.url, form);

      if (res.data.success) {
        toast.success("Patient Registered Successfully");

        setGeneratedUhid(res.data.body.uhidNo);

        // Reset form
        setForm({
          isOldPatient: false,
          docType: "OPD Registration",
          opdNo: "",
          date: "",
          time: "",
          casualty: "No",
          patientName: "",
          guardianName: "",
          houseNo: "",
          street: "",
          village: "",
          district: "",
          gender: "",
          maritalStatus: "",
          aadharNo: "",
          bloodGroup: "",
          mobile: "",
          email: "",
          dob: "",
          age: "",
        });
      } else {
        toast.error("Something went wrong");
      }
    } catch (error) {
      console.log(error);
      toast.error("Server error");
    }
  };

  return (
    <div className="container mt-4">
      <h3 className="mb-4">Patient Registration</h3>

      <form onSubmit={handleSubmit}>
        <div className="row">
          {/* Registration Section */}
          <div className="col-md-3 mb-3">
            <label>Old Patient?</label>
            <select
              className="form-control"
              value={form.isOldPatient ? "Yes" : "No"}
              onChange={(e) =>
                setForm({ ...form, isOldPatient: e.target.value === "Yes" })
              }
            >
              <option>No</option>
              <option>Yes</option>
            </select>
          </div>

          <div className="col-md-3 mb-3">
            <label>UHID No</label>
            <input
              type="text"
              className="form-control"
              value={generatedUhid || "Auto Generated"}
              readOnly
            />
          </div>

          <div className="col-md-3 mb-3">
            <label>OPD No</label>
            <input
              type="text"
              name="opdNo"
              value={form.opdNo}
              onChange={handleChange}
              className="form-control"
            />
          </div>

          <div className="col-md-3 mb-3">
            <label>Date</label>
            <input
              type="date"
              name="date"
              value={form.date}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>

          <div className="col-md-3 mb-3">
            <label>Time</label>
            <input
              type="time"
              name="time"
              value={form.time}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>

          {/* Personal Info */}
          <div className="col-md-6 mb-3">
            <label>Patient Name *</label>
            <input
              type="text"
              name="patientName"
              value={form.patientName}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>

          <div className="col-md-6 mb-3">
            <label>Guardian Name</label>
            <input
              type="text"
              name="guardianName"
              value={form.guardianName}
              onChange={handleChange}
              className="form-control"
            />
          </div>

          <div className="col-md-3 mb-3">
            <label>Date of Birth</label>
            <input
              type="date"
              name="dob"
              value={form.dob}
              onChange={handleDobChange}
              className="form-control"
            />
          </div>

          <div className="col-md-3 mb-3">
            <label>Age</label>
            <input
              type="number"
              value={form.age}
              readOnly
              className="form-control"
            />
          </div>

          <div className="col-md-3 mb-3">
            <label>Gender</label>
            <select
              name="gender"
              value={form.gender}
              onChange={handleChange}
              className="form-control"
              required
            >
              <option value="">Select</option>
              <option>Male</option>
              <option>Female</option>
              <option>Other</option>
            </select>
          </div>

          <div className="col-md-3 mb-3">
            <label>Marital Status</label>
            <select
              name="maritalStatus"
              value={form.maritalStatus}
              onChange={handleChange}
              className="form-control"
            >
              <option value="">Select</option>
              <option>Single</option>
              <option>Married</option>
            </select>
          </div>

          {/* Contact */}
          <div className="col-md-6 mb-3">
            <label>Mobile *</label>
            <input
              type="text"
              name="mobile"
              value={form.mobile}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>

          <div className="col-md-6 mb-3">
            <label>Email</label>
            <input
              type="email"
              name="email"
              value={form.email}
              onChange={handleChange}
              className="form-control"
            />
          </div>

          {/* Address */}
          <div className="col-md-3 mb-3">
            <label>House No</label>
            <input
              type="text"
              name="houseNo"
              value={form.houseNo}
              onChange={handleChange}
              className="form-control"
            />
          </div>

          <div className="col-md-3 mb-3">
            <label>Street</label>
            <input
              type="text"
              name="street"
              value={form.street}
              onChange={handleChange}
              className="form-control"
            />
          </div>

          <div className="col-md-3 mb-3">
            <label>Village</label>
            <input
              type="text"
              name="village"
              value={form.village}
              onChange={handleChange}
              className="form-control"
            />
          </div>

          <div className="col-md-3 mb-3">
            <label>District</label>
            <input
              type="text"
              name="district"
              value={form.district}
              onChange={handleChange}
              className="form-control"
            />
          </div>

          <div className="col-md-3 mb-3">
            <label>Blood Group</label>
            <select
              name="bloodGroup"
              value={form.bloodGroup}
              onChange={handleChange}
              className="form-control"
            >
              <option value="">Select</option>
              <option>A+</option>
              <option>B+</option>
              <option>O+</option>
              <option>AB+</option>
            </select>
          </div>

          <div className="col-md-3 mb-3">
            <label>Aadhar No</label>
            <input
              type="text"
              name="aadharNo"
              value={form.aadharNo}
              onChange={handleChange}
              className="form-control"
            />
          </div>
        </div>

        <button type="submit" className="btn btn-primary mt-3">
          Register Patient
        </button>
      </form>
    </div>
  );
}

export default AddPatient;
