import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import api from "../Backendroutes";

function AdminDashboard() {
  const [activeSection, setActiveSection] = useState("users");

  const [users, setUsers] = useState([]);
  const [doctors, setDoctors] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [staff, setStaff] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [patients, setPatients] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const usersRes = await axios.get(api.getUsers.url);
        const doctorsRes = await axios.get(api.getDoctors.url);
        const appointRes = await axios.get(api.allappointments.url);
        const staffRes = await axios.get(api.allstaff.url);
        const deptRes = await axios.get(api.alldeparment.url);
        const patientRes = await axios.get(api.allpatient.url);

        if (usersRes.data.success) setUsers(usersRes.data.body);
        if (doctorsRes.data.success) setDoctors(doctorsRes.data.body);
        if (appointRes.data.success) setAppointments(appointRes.data.body);
        if (staffRes.data.success) setStaff(staffRes.data.body);
        if (deptRes.data.success) setDepartments(deptRes.data.body);
        if (patientRes.data.success) setPatients(patientRes.data.body);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">Admin Dashboard</h2>

      {/* SECTION BUTTONS */}
      <div className="d-flex flex-wrap justify-content-center gap-3 mb-4">
        <button
          className="btn btn-outline-primary"
          onClick={() => setActiveSection("users")}
        >
          All Users
        </button>
        <button
          className="btn btn-outline-success"
          onClick={() => setActiveSection("doctors")}
        >
          All Doctors
        </button>
        <button
          className="btn btn-outline-info"
          onClick={() => setActiveSection("patients")}
        >
          All Patients
        </button>
        <button
          className="btn btn-outline-warning"
          onClick={() => setActiveSection("departments")}
        >
          Departments
        </button>
        <button
          className="btn btn-outline-secondary"
          onClick={() => setActiveSection("staff")}
        >
          Staff
        </button>
        <button
          className="btn btn-outline-dark"
          onClick={() => setActiveSection("appointments")}
        >
          Appointments
        </button>
      </div>

      {/* USERS */}
      {activeSection === "users" && (
        <>
          <h4>Users ({users.length})</h4>
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Mobile</th>
              </tr>
            </thead>
            <tbody>
              {users.map((u) => (
                <tr key={u._id}>
                  <td>{u.name}</td>
                  <td>{u.email}</td>
                  <td>{u.mobile}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}

      {/* DOCTORS */}
      {activeSection === "doctors" && (
        <>
          <div className="d-flex justify-content-between align-items-center">
            <h4>Doctors ({doctors.length})</h4>
            <Link to="/add-doctor" className="btn btn-primary">
              Add Doctor
            </Link>
          </div>

          <table className="table table-bordered mt-2">
            <thead>
              <tr>
                <th>Name</th>
                <th>Specialization</th>
                <th>Email</th>
                <th>Mobile</th>
              </tr>
            </thead>
            <tbody>
              {doctors.map((d) => (
                <tr key={d._id}>
                  <td>{d.name}</td>
                  <td>{d.specialization}</td>
                  <td>{d.email}</td>
                  <td>{d.mobile}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}

      {/* PATIENTS */}
      {activeSection === "patients" && (
        <>
          <div className="d-flex justify-content-between align-items-center">
            <h4>Patients ({patients.length})</h4>
            <Link to="/add-patient" className="btn btn-info">
              Add Patient
            </Link>
          </div>

          <table className="table table-bordered mt-2">
            <thead>
              <tr>
                <th>UHID</th>
                <th>Name</th>
                <th>Mobile</th>
                <th>Gender</th>
                <th>Blood Group</th>
              </tr>
            </thead>
            <tbody>
              {patients.map((p) => (
                <tr key={p._id}>
                  <td>{p.uhidNo}</td>
                  <td>{p.patientName}</td>
                  <td>{p.mobile}</td>
                  <td>{p.gender}</td>
                  <td>{p.bloodGroup}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}

      {/* DEPARTMENTS */}
      {activeSection === "departments" && (
        <>
          <h4>Departments ({departments.length})</h4>
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Name</th>
                <th>Created</th>
              </tr>
            </thead>
            <tbody>
              {departments.map((d) => (
                <tr key={d._id}>
                  <td>{d.name}</td>
                  <td>{new Date(d.createdAt).toLocaleDateString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}

      {/* STAFF */}
      {activeSection === "staff" && (
        <>
          <h4>Staff ({staff.length})</h4>
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Name</th>
                <th>Role</th>
                <th>Email</th>
                <th>Mobile</th>
              </tr>
            </thead>
            <tbody>
              {staff.map((s) => (
                <tr key={s._id}>
                  <td>{s.name}</td>
                  <td>{s.role}</td>
                  <td>{s.email}</td>
                  <td>{s.mobile}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}

      {/* APPOINTMENTS */}
      {activeSection === "appointments" && (
        <>
          <h4>Appointments ({appointments.length})</h4>
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>User</th>
                <th>Doctor</th>
                <th>Date</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {appointments.map((a) => (
                <tr key={a._id}>
                  <td>{a.userId?.name}</td>
                  <td>{a.doctorId?.name}</td>
                  <td>{new Date(a.date).toLocaleString()}</td>
                  <td>{a.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
}

export default AdminDashboard;
